<?phprequire('../conexion.php');header('Content-Type: text/html; charset=iso-8859-1');$area=$_POST['area'];$sql="SELECT dominio, id_dominio FROM dominio WHERE id_area='$area' ORDER BY dominio";$recordset = mysql_query($sql);echo '<select id="dominio" name="dominio" onchange="filtrar_objetivo();" style="font-size:15">';echo '<option value="0">General</option>';for($i=1;$i<=mysql_num_rows($recordset);$i++){	$fila=mysql_fetch_array($recordset);	echo '<option value="'.$fila['id_dominio'].'">'.$fila['dominio'].'</option>';}echo '</select>';?>