<?phpheader('Content-Type: text/html; charset=iso-8859-1');require('../conexion.php');$dominio=$_POST['dominio'];$sql="SELECT objetivo, id_objetivo FROM objetivos WHERE id_dominio='$dominio' ORDER BY objetivo";$recordset = mysql_query($sql);echo '<select id="objetivo" name="objetivo" style="font-size:15">';echo '<option value="0">General</option>';for($i=1;$i<=mysql_num_rows($recordset);$i++){	$fila=mysql_fetch_array($recordset);	echo '<option value="'.$fila['id_objetivo'].'">'.$fila['objetivo'].'</option>';}echo '</select>';?>