<?phprequire('../conexion.php');$servicio1=$_POST['servicio1'];$sql="SELECT servicio2, id_serv2 FROM t_servicio2 WHERE servicio_cod='$servicio1' ORDER BY servicio2";$recordset = mysql_query($sql);echo '<select id="servicio2" name="servicio2" style="font-size:9">';echo '<option value="0">Seleccionar</option>';while($fila=mysql_fetch_array($recordset)){	if($fila['id_serv2']==$servicio1)		echo '<option value="'.$fila['id_serv2'].'" selected>'.$fila['servicio2'].'</option>';	else		echo '<option value="'.$fila['id_serv2'].'">'.$fila['servicio2'].'</option>';}echo '</select>';//echo $filaV['nivel2'];?>