<?phprequire('../conexion.php');$IdProv=$_POST['opt'];$servicio1=$_POST['servicio1'];$sqlV="SELECT nivel2 FROM proveedor WHERE IdProv='$IdProv'";$resultV=mysql_query($sqlV);$filaV=mysql_fetch_array($resultV);$sql="SELECT servicio2, id_serv2 FROM t_servicio2 WHERE servicio_cod='$servicio1' ORDER BY servicio2";$recordset = mysql_query($sql);echo '<select id="servicio2" name="servicio2" style="font-size:9">';echo '<option value="0">Seleccionar</option>';while($fila=mysql_fetch_array($recordset)){	if($fila['id_serv2']==$filaV['nivel2'])		echo '<option value="'.$fila['id_serv2'].'" selected>'.$fila['servicio2'].'</option>';	else		echo '<option value="'.$fila['id_serv2'].'">'.$fila['servicio2'].'</option>';}echo '</select>';//echo $servicio1;?>